mapper.select:
SELECT

FROM sys_menu a

WHERE a.id = #{id}

SELECT
FROM sys_menu a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' CONCAT('%/',#{name},'%') ESCAPE '/'
AND a.resource_type = #{resourceType}
ORDER BY a.create_date DESC

mapper.update:
UPDATE sys_menu SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE sys_menu SET 
	sort = #{sort}
WHERE id = #{id}
